import Button from "./Button"
import styled from "./App.module.css"
import { useState, useEffect} from "react"

function Hello()
{
  const [showing, setShowing] = useState(false)
  useEffect(()=>{
    console.log("im here")
  }, [])
  return <h1>Hello</h1>;
}

function App() {
  const [showing, setShowing] = useState(false)
  const onClick = () => setShowing((prev)=> !prev)
  return (
    <div className="App">
      <button
        onClick={onClick}>
          {showing? "Hide" : "Show"}
      </button>
      {showing? <Hello /> : null}
    </div>
  );
}

export default App;
