import Button from "./Button"
import styled from "./App.module.css"
import { useState, useEffect} from "react"

function Hello()
{
  const [shoowing, setShoowing] = useState(false)
  const oonClick = () => setShoowing((prev)=> !prev)
  useEffect(()=>{
    console.log("created!")
    return () => console.log("destroyed!")
  }, [])
  return (
    <div className="App">
      <h1>Hello</h1>
      <button
        onClick={oonClick}>
          {shoowing? "a" : "b"}
      </button>
    </div>
  );
}

function App() {
  const [showing, setShowing] = useState(false)
  const onClick = () => setShowing((prev)=> !prev)
  return (
    <div className="App">
    {showing? <Hello /> : null}
      <button
        onClick={onClick}>
          {showing? "Hide" : "Show"}
      </button>
    </div>
  );
}

export default App;
